mapper.insert:
  INSERT
    INTO folders
       ( member_id
       , folder_name
       , depth
       , seq
       )
VALUES ( #{memberId}
       , #{folderForm.folderName}
       , #{folderForm.depth}
       , #{folderForm.seq}
       )

mapper.update:
UPDATE folders
   SET folder_name = #{folderForm.folderName}
 WHERE folder_id = #{folderId}
   AND member_id = #{memberId}

mapper.select:
SELECT folder_id
  FROM folders
 WHERE member_id        = #{memberId}
   AND folder_id IN

      #{id}

update.foreach:
UPDATE folders
   SET parent_folder_id = #{folder.parentFolderId}
     , depth = #{folder.depth}
     , seq = #{folder.seq}
 WHERE folder_id = #{folder.folderId}
   AND member_id = #{memberId}

mapper.update:
UPDATE folders
   SET deleted_at   = CURRENT_TIMESTAMP
 WHERE folder_id    = #{folderId}
