mapper.insert:
INSERT
  INTO kanban_cards
     ( kanban_column_id
     , title
     , content
     , card_seq
     , priority
     , task_size
     , start_at
     , end_at
     )
VALUES
     ( #{kanbanColumnId}
     , #{kanbanCardForm.title}
     , #{kanbanCardForm.content}
     , 0
     , #{kanbanCardForm.priority}
     , #{kanbanCardForm.taskSize}
     , #{kanbanCardForm.startAt}
     , #{kanbanCardForm.endAt}
     )

INSERT
  INTO kanban_works
     ( kanban_card_id
     , member_id
     )
VALUES

     ( #{kanbanCardId}
     , #{memberId}
     )

mapper.select:
SELECT COUNT(*)
FROM kanban_columns
WHERE kanban_column_id = #{kanbanColumnId}
