mapper.insert:
INSERT
  INTO kanban_cards
     ( kanban_column_id
     , title
     , content
     , card_seq
     , priority
     , task_size
     , start_at
     , end_at
     )
VALUES
     ( #{kanbanColumnId}
     , #{kanbanCardForm.title}
     , #{kanbanCardForm.content}
     , 0
     , #{kanbanCardForm.priority}
     , #{kanbanCardForm.taskSize}
     , #{kanbanCardForm.startAt}
     , #{kanbanCardForm.endAt}
     )

INSERT
  INTO kanban_works
     ( kanban_card_id
     , member_id
     )
VALUES

     ( #{kanbanCardId}
     , #{memberId}
     )

update.foreach:
UPDATE kanban_columns
SET column_seq = #{column.columnSeq}
WHERE kanban_column_id = #{column.kanbanColumnId}

mapper.update:
UPDATE kanban_columns
SET column_seq = column_seq - 1
WHERE project_id = #{projectId}
  AND column_seq > #{deletedColumnSeq}
  AND deleted_at IS NULL;
