mapper.insert:
  INSERT
    INTO projects
       ( project_name
       , project_description
       , start_at
       , end_at
       )
VALUES ( #{projectName}
       , #{projectDescription}
       , #{startAt}
       , #{endAt}
       )

mapper.update:
UPDATE projects
   SET project_name = #{projectForm.projectName}
     , project_description = #{projectForm.projectDescription}
     , start_at = #{projectForm.startAt}
     , end_at = #{projectForm.endAt}
 WHERE project_id = #{projectId}

mapper.select:
SELECT project_id
  FROM project_joins
 WHERE member_id = #{userId}
